mapper.select:
SELECT COUNT(*) 
  FROM member_info 
 WHERE mem_id= #{mem_id}

SELECT COUNT(*) 
  FROM member_info 
 WHERE mem_id= #{mem_id} AND passwd = #{passwd}

SELECT COUNT(*)
  FROM wordcloud_list
 WHERE word = #{word}

mapper.update:
UPDATE wordcloud_list
   SET count = count + #{count}
 WHERE word = #{word}
